mapper.select:
SELECT PRO_MEM_NUM
FROM MBS_PRO_MEMBER
WHERE PRO_MEM_PHONE_MID || PRO_MEM_PHONE_BACK = #{proMemPhone} AND PRO_MEM_PASSWORD = #{proMemPassword}

SELECT PRO_MEM_PHONE_MID || PRO_MEM_PHONE_BACK, PRO_MEM_NUM, PRO_MEM_NAME
FROM MBS_PRO_MEMBER
WHERE PRO_MEM_PHONE_MID || PRO_MEM_PHONE_BACK = #{proMemPhone} AND PRO_MEM_PASSWORD = #{proMemPassword}
